package hello.core;import lombok.Getter;import lombok.Setter;import lombok.ToString;import lombok.extern.slf4j.Slf4j;@Slf4j@Getter@Setter@ToStringpublic class HelloLombok {    private String name;    private int age;    public static void main(final String[] args) {        final var helloLombok = new HelloLombok();        helloLombok.setName("name");        final String name = helloLombok.getName();        log.info("name = {}", name);    }}